import React, { useState, useEffect } from "react";
import "./explore.css";
import { Container, Row, Col } from "react-bootstrap";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/scss";
import "swiper/scss/navigation";
import "swiper/scss/pagination";
import DiscoverLogo from "../../../src/assets/discover-logo.svg";
import Check from "../../../src/assets/check.svg";
import SwiperCore, { Pagination, Navigation } from "swiper/core";
import { top_seller,hot_bids } from "../../json";

SwiperCore.use([Pagination, Navigation]);
console.log('topseller dışarı',top_seller)
console.log('hot_bids dışarı',hot_bids)

const Explore = () => {
  const [topSeller, setTopSeller] = useState([]);
  const [hotBids, setHotBids] = useState([]);
  useEffect(() => {
    setTopSeller(top_seller);
    setHotBids(hot_bids)
  },[]);

  return (
    <Container fluid="md">
      <Col>
        <Row
          sm={3}
          className="text-center justify-content-center align-items-center"
        >
          <img className="headerImg" src={DiscoverLogo} alt="React Logo" />
        </Row>
        <Row sm={3} className="mt-5">
          <Col sm={12}><span className="label-header">Top Sellers</span></Col>
          <Col sm={12} className="mt-5">
            <Swiper
              slidesPerView={3}
              centeredSlides={true}
              spaceBetween={30}
              navigation={true}
              className="mySwiper"
            >
              {topSeller.length>0 &&
                topSeller.map((element, index) => (
                  <SwiperSlide key={index}>
                    <div className="slide-container">
                       <div className="slide-number mb-1">{element.id}</div>
                       <div className="slide-icon-container mb-1"> <img className="user-icon" src={element.avatar} alt="React Logo" /> <img className="check-icon" src={Check} alt="check"></img></div>
                       <span className="slider-name mb-1">{element.name}</span>
                       <span className="mb-1"><strong>5250 </strong>EHT</span>
                    </div>
                  </SwiperSlide>
                ))}
            </Swiper>
          </Col>
        </Row>
        <Row sm={6}>
        <Col sm={12}><span className="label-header">Hot Bids</span></Col>
        <Col sm={3}>
        {hotBids.length>0 &&
                hotBids.map((element, index) => (
          <div className="hotbid-card"
          key={index}
          >
          <img className="user-icon" src={element.image} alt="React Logo" />
          <span className="slider-name mb-1">{element.name}</span>
          </div>
            ))}
        </Col>
        </Row>
      </Col>
    </Container>
  );
};

export default Explore;
