import React, { useState, useEffect } from "react";
import "./explore.css";
import { Container, Row, Col } from "react-bootstrap";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/scss";
import "swiper/scss/navigation";
import "swiper/scss/pagination";
import DiscoverLogo from "../../../src/assets/discover-logo.svg";
import SwiperCore, { Pagination, Navigation } from "swiper/core";
import { top_seller } from "../../json";
// install Swiper modules
SwiperCore.use([Pagination, Navigation]);

const Explore = () => {
  const [topSeller, setTopSeller] = useState([]);
  useEffect(() => {
    if (top_seller) setTopSeller(top_seller);
  }, []);
  useEffect(() => {
    console.log("topseller", topSeller);
  }, [topSeller]);

  return (
    <Container fluid="md">
      <Col>
        <Row
          sm={3}
          className="text-center justify-content-center align-items-center"
        >
          <img className="headerImg" src={DiscoverLogo} alt="React Logo" />
        </Row>
        <Row sm={3} className="mt-5">
          {" "}
          <>
            <Swiper
              slidesPerView={3}
              centeredSlides={true}
              spaceBetween={30}
              pagination={{
                type: "fraction",
              }}
              navigation={true}
              className="mySwiper"
            >
              {topSeller &&
                topSeller.map((element, index) => (
                  <SwiperSlide key={index}>{element}</SwiperSlide>
                ))}
            </Swiper>
          </>
        </Row>
        <Row sm={6}>sm=6</Row>
      </Col>
    </Container>
  );
};

export default Explore;
